INSERT INTO `sd_case_about` (`case_id`, `cat_id`, `title`, `sort_order`, `keyword`, `content`, `title_en`, `keyword_en`, `content_en`, `title_tw`, `keyword_tw`, `content_tw`, `g_pic`, `addtime`, `author`, `click`, `updatetime`) VALUES
(1, 0, '日先简介', 0, 'sdfgsdf', '<h4>\r\n	日先简介</h4>\r\n<p>\r\n	<em>超过20年的日先</em></p>\r\n<p>\r\n	日先凭借丰富的经验不断创新思维，通过市场调研，开发客户需求，将&ldquo;产品陈列导向&rdquo;升级为&ldquo;氛围体验导向&rdquo;，提出&ldquo;要开店，找日先&rdquo;，通过多元化设计为客户提升品牌形象。</p>\r\n<br />\r\n<p>\r\n	日先认为理想的商业零售店铺是效果显著、并能被快速大规模低成本复制，实现销售增长的店铺。在此思路的指引下，日先推出&ldquo;全国开店，千店一面&rdquo;，提供品牌店铺空间规划、设计、制造、测绘、配送、安装、装修及维护全方位店铺服务。</p>\r\n<br />\r\n<p>\r\n	360度全方位品牌店铺服务，具备极佳的品牌张力、开店综合性价比、开店执行力、店铺销售力、对市场消费者需求的精准锁定度，从设计到执行，到全国推广，实现更好的效果、更快速、更大规模、更低成本！</p>\r\n<br />\r\n<p>\r\n	目前已拥有国内一流的设计团队，华南、华东、华北三大区域的综合服务中心，服务网络遍布全国，覆盖20000余家品牌店铺，预计两年内网点数量可扩展到400个，为品牌客户在较短时间内实现品牌店铺同步升级提供了可能。</p>\r\n<br />\r\n<p>\r\n	合作十年，日先成为宝洁全球策略性合作伙伴，由于成绩卓著，2008年获宝洁副总裁Stew Atkinson先生颁发最佳供应商奖，日先的D-max等多个项目已在全球P&amp;G 推广，并逐步切入其亚太区的供应代理业务；此外，2009年荣获美的集团授予优秀合作伙伴的荣誉称号；为海尔成功打造出全品类高端形象店，成为其指定的品牌店铺形象升级战略服务商&hellip;&hellip;</p>\r\n<br />\r\n<p>\r\n	现在，日先继续为宝洁、麦当劳、诺基亚、屈臣氏、海尔、美的、李宁、九阳、联想等众多国际知名品牌的品牌店铺升级提供服务； 未来，日先将全心致力于更多品牌企业的品牌店铺升级服务。</p>\r\n<br />\r\n<p>\r\n	<em>日先现任主席兼总裁</em> <img src="http://127.0.0.1/rising/public/images/intro_07.jpg" /></p>\r\n<p>\r\n	从2010年5月1日起，施文圣(Shannan Stevenson)先生担任宝洁公司大中华区总裁职务。施文圣先生出生于英国，于1987年加入宝洁公司，在公司的多个业务部门工作过，包括织物护理、健康及美容、婴儿护理及妇女护理等，工作地点包括英国、土尔其、俄罗斯和埃及等。阳、联想等众多国际知名品牌的品牌店铺升级提供服务； 未来，日先将全心致力于更多品牌企业的品牌店铺升级服务。</p>\r\n', '', '', '', '', '', '', '', 0, NULL, 0, 1386578044),
(2, 0, '日先足迹', 0, 'strategy', '<h4>\r\n	日先中国-filemtime</h4>\r\n<p>\r\n	<em>超过20年的日先</em></p>\r\n<p>\r\n	日先公司在全球80多个国家和地区开展业务。2012财政年度，公司全年销售额近840亿美元。宝洁公司在全球80多个国家设有工厂或分公司，所经营的300多个品牌的产品畅销160多个国家和地区，其中包括美容美发、居家护理、家庭健康用品等。</p>\r\n<br />\r\n<p>\r\n	<em>日先概况</em></p>\r\n<div class="details">\r\n	<p>\r\n		<span>成立时间</span> 1993</p>\r\n	<p>\r\n		<span>地点</span> 中山市港口镇</p>\r\n	<p>\r\n		<span>销售额</span> 13亿元</p>\r\n	<p>\r\n		<span>分公司分布</span> 北京、上海等26个省份</p>\r\n	<p>\r\n		<span>产品种类</span> 电子，服装，数码等</p>\r\n	<p>\r\n		<span>合作品牌</span> 宝洁、海尔、美的、苏宁......</p>\r\n</div>\r\n<p>\r\n	<em>历史瞬间</em></p>\r\n<div class="moment">\r\n	<p>\r\n		在日先，我们致力于和使用我们产品的人们，和帮助我们 达成目标的合作伙伴保持联系和互动。</p>\r\n	<span><a href="#">保持领先</a> <a href="#">成为领导者</a> <a href="#">升级形象</a> </span></div>\r\n', '', '', '', '', '', '', '', 0, NULL, 0, 1387163642),
(3, 0, '日先荣誉', 0, 'setiosflags', '<h4>\r\n	企业荣誉</h4>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_03.png" />\r\n	<div class="honorl">\r\n		&middot; 日先儿童安全饮用水项目获《经济学人》创新奖</div>\r\n</div>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_06.png" />\r\n	<div class="honorl">\r\n		&middot; &middot; 与百度合作项目&ldquo;感谢妈妈 用爱跨越距离&rdquo;获2012年 第五届金投赏全场大奖.</div>\r\n</div>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_09.png" />\r\n	<div class="honorl">\r\n		&middot; &middot; 2012年跨国公司本地创新20强&rdquo;榜单第二位</div>\r\n</div>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_11.png" />\r\n	<div class="honorl">\r\n		&middot; &middot; 2012国际绿色设计贡献奖</div>\r\n</div>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_13.png" />\r\n	<div class="honorl">\r\n		&middot; &middot; 环球企业家&ldquo;2012年度创新营销大奖&rdquo;</div>\r\n</div>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_15.png" />\r\n	<div class="honorl">\r\n		&middot;&middot; 2012年度体育营销十大经典案例</div>\r\n</div>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_17.png" />\r\n	<div class="honorl">\r\n		&middot;&middot; 新浪&ldquo;微博年度影响力企业&rdquo;大奖</div>\r\n</div>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_20.png" />\r\n	<div class="honorl">\r\n		&middot; 与华润万家合作项目获&ldquo;CCFA2012中国零售创新奖&rdquo;</div>\r\n</div>\r\n<div class="honor">\r\n	<img src="http://localhost/rising/public/images/honor_22.png" />\r\n	<div class="honorl">\r\n		&middot; 日先儿童安全饮用水项目获《经济学人》创新奖</div>\r\n</div>\r\n', '', '', '', '', '', '', '', 0, NULL, 0, 1386578661),
(4, 0, '日先理念', 0, 'setiosflags', '<h4>\r\n	日先理念</h4>\r\n<p>\r\n	<em>创新</em></p>\r\n<p>\r\n	提供卓越店铺服务，确保品牌持续增长</p>\r\n<br />\r\n<p>\r\n	为现在和未来的世世代代，提供优质超值的品牌产品和服务，在全世界更多的地方，更全面的，亲近和美化更多消费者的生活。</p>\r\n<br />\r\n<p>\r\n	作为回报，我们将会获得领先的市场销售地位、不断增长的利润和价值，从而令我们的员工、股东以及我们生活和工作所处的社会共同繁荣。</p>\r\n<br />\r\n<p>\r\n	<em>奋进</em></p>\r\n<p>\r\n	值得信赖的品牌店铺服务领导者.</p>\r\n<br />\r\n<p>\r\n	宝洁公司，就是宝洁人以及他们遵从的价值观。我们吸引和招聘世界上最优秀的人才。我们实行从内部发展的组织制度, 选拔、提升和奖励表现突出的员工而不受任何与工作表现无关的因素影响。我们坚信，宝洁的所有员工始终是公司最为宝贵的财富。</p>\r\n<br />\r\n<p>\r\n	<em>支持</em></p>\r\n<p>\r\n	创新 奋进 支持 守信 .</p>\r\n<br />\r\n<p>\r\n	我们重视公司外部环境的变化和发展</p>\r\n<p>\r\n	我们力求最好地了解消费者及其需要。</p>\r\n<p>\r\n	我们创造和提供一流的产品和包装，倡导全新的消费观念，树立成功的品牌形象。</p>\r\n<p>\r\n	我们发展与客户、供应商之间紧密互惠的关系。</p>\r\n<p>\r\n	我们的公司是有良好素质的法人。</p>\r\n<p>\r\n	我们将可持续性融入我们的产品、包装和运营。</p>\r\n<br />\r\n<p>\r\n	<em>守信</em></p>\r\n<p>\r\n	创新 奋进 支持 守信 .</p>\r\n<br />\r\n<p>\r\n	我们重视公司外部环境的变化和发展</p>\r\n<p>\r\n	我们力求最好地了解消费者及其需要。</p>\r\n<p>\r\n	我们创造和提供一流的产品和包装，倡导全新的消费观念，树立成功的品牌形象。</p>\r\n<p>\r\n	我们发展与客户、供应商之间紧密互惠的关系。</p>\r\n<p>\r\n	我们的公司是有良好素质的法人。</p>\r\n<p>\r\n	我们将可持续性融入我们的产品、包装和运营。</p>\r\n<br />\r\n<p>\r\n	我们珍视个人的专长</p>\r\n<br />\r\n', '', '', '', '', '', '', '', 0, NULL, 0, 1386581553),
(5, 7, '1993-filemtime', 0, '', '<p>\r\n	日先公司开创性地提倡利润分享计划，让员工拥有 公司的股权。这一重大创新促使员工把自己的重要 作用和公司的成功联系起来。</p>\r\n', '', '', '', '', '', '', '', 1387161008, 'wing', 0, 1387162242),
(6, 7, '1994-filemtime', 0, '', '<p>\r\n	2日先公司开创性地提倡利润分享计划，让员工拥有 公司的股权。这一重大创新促使员工把自己的重要 作用和公司的成功联系起来。</p>\r\n', '', '', '', '', '', '', '', 1387161059, 'wing', 0, 1387162210),
(9, 8, '宗旨、价值观及原则', 0, '', '<p>\r\n	<img class="imgalign" src="http://localhost/rising/public/images/about_10.jpg" /></p>\r\n<p>\r\n	日先的宗旨是在现在和未来的世世代代确保每个人有更高的生活质量。宗旨是引领，为我们公司的抉择及行动带来了灵感与指导。</p>\r\n<p>\r\n	日先在全球范围内举足轻重。我们的市场资本额超过许多国家的国内生产总值，我们的消费者遍布180个国家和地区。庞大规模带来的是责任和机遇。我们的责任是做一个符合社会道德要求的企业公民，在现在和未来的世世代代确保每个人有更高的生活质量。我们的机遇则更高瞻远瞩，并与我们的&ldquo;目标&rdquo;息息相关。</p>\r\n', '', '', '', '', '', '', '', 1387167212, 'wing', 0, 1387176978),
(10, 8, '日先中国', 0, '', '                                <p>通过坚持用细微但有意义的方式美化消费者每一天的生活----宝洁公司得以176年保持持续的增长。宝洁公司拥有众多深受信赖的优质领先品牌。宝洁公司在全球80多个国家设有工厂或分公司。</p>\r\n', '', '', '', '', '', '', '', 1387167243, 'wing', 0, 0),
(11, 8, '企业荣耀', 0, '', '                                <p>宝洁感谢所有消费者，正是你们长久以来的支持和信任，使我们能够持续赢得社会认可，成为一家值得尊敬和信任的公司。我们将一如既往地发挥在产品、环境、公益和社会等方面的高度责任心，让你们的生活更美好。</p>\r\n', '', '', '', '', '', '', '', 1387167765, 'wing', 0, 0),
(12, 8, '联系日先', 0, '', '                                <p>谢谢你愿意与我们联络，我们乐于为你效劳！你可以根据需要通过服务专线等方式直接与我们联络。</p>\r\n', '', '', '', '', '', '', '', 1387167816, 'wing', 0, 0);
